// Helper maps for O(1) lookups
export const districtsByState = new Map<string, DistrictEntry[]>();
export const citiesByStateDistrict = new Map<string, CityOption[]>();

indianStates.forEach(state => {
  districtsByState.set(state.value, state.districts);
  state.districts.forEach(district => {
    const key = `${state.value}::${district.value}`;
    citiesByStateDistrict.set(key, district.cities);
  });
});

export const getDistrictsForState = (stateValue: string): DistrictEntry[] => {
  return districtsByState.get(stateValue) || [];
};

export const getCitiesForDistrict = (stateValue: string, districtValue: string): CityOption[] => {
  const key = `${stateValue}::${districtValue}`;
  return citiesByStateDistrict.get(key) || [];
};
